import React, { useState, useEffect } from 'react';
import { useWebSocket } from '../hooks/useWebSocket';
import { 
  ServerIcon, 
  UserGroupIcon, 
  BellIcon, 
  GlobeAltIcon,
  ChartBarIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon
} from '@heroicons/react/24/outline';
import { LineChart, Line, AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';

export default function Dashboard() {
  const { metrics, events, agents, isConnected } = useWebSocket();
  const [systemMetrics, setSystemMetrics] = useState(null);

  useEffect(() => {
    if (metrics) {
      setSystemMetrics(metrics);
    }
  }, [metrics]);

  // Mock data for charts
  const cpuData = [
    { time: '00:00', cpu: 45 },
    { time: '04:00', cpu: 52 },
    { time: '08:00', cpu: 78 },
    { time: '12:00', cpu: 65 },
    { time: '16:00', cpu: 89 },
    { time: '20:00', cpu: 72 },
    { time: '24:00', cpu: 58 },
  ];

  const memoryData = [
    { time: '00:00', memory: 60 },
    { time: '04:00', memory: 65 },
    { time: '08:00', memory: 75 },
    { time: '12:00', memory: 80 },
    { time: '16:00', memory: 85 },
    { time: '20:00', memory: 78 },
    { time: '24:00', memory: 70 },
  ];

  const eventTypes = [
    { name: 'System', value: 45, color: '#3B82F6' },
    { name: 'Security', value: 25, color: '#EF4444' },
    { name: 'Network', value: 20, color: '#10B981' },
    { name: 'Agent', value: 10, color: '#F59E0B' },
  ];

  const getStatusColor = (status) => {
    switch (status) {
      case 'healthy':
        return 'text-success-600 bg-success-100 dark:bg-success-900 dark:text-success-200';
      case 'warning':
        return 'text-warning-600 bg-warning-100 dark:bg-warning-900 dark:text-warning-200';
      case 'error':
        return 'text-danger-600 bg-danger-100 dark:bg-danger-900 dark:text-danger-200';
      default:
        return 'text-gray-600 bg-gray-100 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'critical':
        return 'text-danger-600 bg-danger-100 dark:bg-danger-900 dark:text-danger-200';
      case 'error':
        return 'text-danger-600 bg-danger-100 dark:bg-danger-900 dark:text-danger-200';
      case 'warning':
        return 'text-warning-600 bg-warning-100 dark:bg-warning-900 dark:text-warning-200';
      case 'info':
        return 'text-primary-600 bg-primary-100 dark:bg-primary-900 dark:text-primary-200';
      default:
        return 'text-gray-600 bg-gray-100 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  return (
    <div className="space-y-6">
      {/* Page header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900 dark:text-white">Dashboard</h1>
        <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          Real-time system overview and metrics
        </p>
      </div>

      {/* Connection status */}
      {!isConnected && (
        <div className="bg-warning-50 dark:bg-warning-900 border border-warning-200 dark:border-warning-700 rounded-lg p-4">
          <div className="flex">
            <ExclamationTriangleIcon className="h-5 w-5 text-warning-400" />
            <div className="ml-3">
              <h3 className="text-sm font-medium text-warning-800 dark:text-warning-200">
                Connection Lost
              </h3>
              <p className="mt-1 text-sm text-warning-700 dark:text-warning-300">
                Real-time updates are currently unavailable. Attempting to reconnect...
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Key metrics */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        <MetricCard
          title="System Status"
          value={systemMetrics?.watchtower?.system_health || 'Unknown'}
          icon={ServerIcon}
          status={systemMetrics?.watchtower?.system_health}
          change="+2.5%"
          changeType="positive"
        />
        <MetricCard
          title="Active Agents"
          value={systemMetrics?.watchtower?.active_agents || 0}
          icon={UserGroupIcon}
          change="+12"
          changeType="positive"
        />
        <MetricCard
          title="Total Events"
          value={systemMetrics?.watchtower?.total_events || 0}
          icon={BellIcon}
          change="+1,234"
          changeType="positive"
        />
        <MetricCard
          title="Connected Peers"
          value={systemMetrics?.watchtower?.peers_count || 0}
          icon={GlobeAltIcon}
          change="+1"
          changeType="positive"
        />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 gap-6 lg:grid-cols-2">
        {/* CPU Usage */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white">CPU Usage</h3>
          </div>
          <div className="card-body">
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <AreaChart data={cpuData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis />
                  <Tooltip />
                  <Area type="monotone" dataKey="cpu" stroke="#3B82F6" fill="#3B82F6" fillOpacity={0.3} />
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>

        {/* Memory Usage */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white">Memory Usage</h3>
          </div>
          <div className="card-body">
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <AreaChart data={memoryData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="time" />
                  <YAxis />
                  <Tooltip />
                  <Area type="monotone" dataKey="memory" stroke="#10B981" fill="#10B981" fillOpacity={0.3} />
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>

      {/* System metrics and events */}
      <div className="grid grid-cols-1 gap-6 lg:grid-cols-3">
        {/* Event Types */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white">Event Types</h3>
          </div>
          <div className="card-body">
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={eventTypes}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {eventTypes.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>

        {/* Recent Events */}
        <div className="card lg:col-span-2">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white">Recent Events</h3>
          </div>
          <div className="card-body">
            <div className="space-y-4 max-h-64 overflow-y-auto">
              {events.slice(0, 10).map((event, index) => (
                <div key={index} className="flex items-start space-x-3">
                  <div className={`flex-shrink-0 w-2 h-2 mt-2 rounded-full ${
                    event.severity === 'critical' || event.severity === 'error' ? 'bg-danger-500' :
                    event.severity === 'warning' ? 'bg-warning-500' : 'bg-primary-500'
                  }`} />
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900 dark:text-white">
                      {event.message}
                    </p>
                    <p className="text-sm text-gray-500 dark:text-gray-400">
                      {event.source} â€¢ {new Date(event.timestamp).toLocaleString()}
                    </p>
                  </div>
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getSeverityColor(event.severity)}`}>
                    {event.severity}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* System health indicators */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-medium text-gray-900 dark:text-white">System Health</h3>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
            <HealthIndicator
              title="CPU"
              value={`${systemMetrics?.system?.cpu_percent || 0}%`}
              status={systemMetrics?.system?.cpu_percent > 80 ? 'warning' : 'healthy'}
            />
            <HealthIndicator
              title="Memory"
              value={`${systemMetrics?.system?.memory_percent || 0}%`}
              status={systemMetrics?.system?.memory_percent > 85 ? 'warning' : 'healthy'}
            />
            <HealthIndicator
              title="Disk"
              value={`${systemMetrics?.system?.disk_percent || 0}%`}
              status={systemMetrics?.system?.disk_percent > 90 ? 'warning' : 'healthy'}
            />
            <HealthIndicator
              title="Network"
              value="Active"
              status="healthy"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

function MetricCard({ title, value, icon: Icon, status, change, changeType }) {
  return (
    <div className="metric-card">
      <div className="flex items-center">
        <div className="flex-shrink-0">
          <Icon className="h-8 w-8 text-gray-400" aria-hidden="true" />
        </div>
        <div className="ml-5 w-0 flex-1">
          <dl>
            <dt className="metric-label">{title}</dt>
            <dd className="flex items-baseline">
              <div className="metric-value">{value}</div>
              {change && (
                <div className={`ml-2 flex items-baseline text-sm font-semibold ${
                  changeType === 'positive' ? 'metric-change-positive' : 'metric-change-negative'
                }`}>
                  {change}
                </div>
              )}
            </dd>
          </dl>
        </div>
      </div>
    </div>
  );
}

function HealthIndicator({ title, value, status }) {
  const getStatusIcon = (status) => {
    switch (status) {
      case 'healthy':
        return <CheckCircleIcon className="h-5 w-5 text-success-500" />;
      case 'warning':
        return <ExclamationTriangleIcon className="h-5 w-5 text-warning-500" />;
      case 'error':
        return <ExclamationTriangleIcon className="h-5 w-5 text-danger-500" />;
      default:
        return <CheckCircleIcon className="h-5 w-5 text-gray-400" />;
    }
  };

  return (
    <div className="flex items-center space-x-3">
      {getStatusIcon(status)}
      <div>
        <p className="text-sm font-medium text-gray-900 dark:text-white">{title}</p>
        <p className="text-sm text-gray-500 dark:text-gray-400">{value}</p>
      </div>
    </div>
  );
}
