import React, { useState } from 'react';
import { Bars3Icon, CalculatorIcon, UserIcon } from '@heroicons/react/24/outline';
import { useWebSocket } from '../hooks/useWebSocket';

export default function Header({ onMenuClick }) {
  const { isConnected } = useWebSocket();
  const [showCalculator, setShowCalculator] = useState(false);
  const [showAdminMenu, setShowAdminMenu] = useState(false);

  const handleCalculatorClick = () => {
    setShowCalculator(!showCalculator);
    // You can add actual calculator functionality here
    alert('Calculator functionality coming soon!');
  };

  const handleAdminClick = () => {
    setShowAdminMenu(!showAdminMenu);
  };

  return (
    <header className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
      <div className="flex items-center justify-between h-16 px-4 sm:px-6 lg:px-8">
        {/* Left side */}
        <div className="flex items-center">
          <button
            type="button"
            className="lg:hidden -m-2.5 p-2.5 text-gray-700 dark:text-gray-300"
            onClick={onMenuClick}
          >
            <span className="sr-only">Open sidebar</span>
            <Bars3Icon className="h-6 w-6" aria-hidden="true" />
          </button>
        </div>

        {/* Right side */}
        <div className="flex items-center space-x-4">
          {/* Connection status */}
          <div className="flex items-center space-x-2">
            <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-success-500' : 'bg-danger-500'}`} />
            <span className="text-sm text-gray-600 dark:text-gray-300">
              {isConnected ? 'Connected' : 'Disconnected'}
            </span>
          </div>

          {/* Calculator button */}
          <button 
            onClick={handleCalculatorClick}
            className="p-2 text-gray-400 hover:text-gray-500 dark:hover:text-gray-300"
            title="Calculator"
          >
            <span className="sr-only">Calculator</span>
            <CalculatorIcon className="h-6 w-6" />
          </button>

          {/* Notifications */}
          <button className="p-2 text-gray-400 hover:text-gray-500 dark:hover:text-gray-300">
            <span className="sr-only">View notifications</span>
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5 5v-5z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 7h6m0 10v-3m-3 3h.01M9 17h.01M9 14h.01M12 14h.01M15 11h.01M12 11h.01M9 11h.01M7 21h10a2 2 0 002-2V5a2 2 0 00-2-2H7a2 2 0 00-2 2v14a2 2 0 002 2z" />
            </svg>
          </button>

          {/* User/Admin menu */}
          <div className="relative">
            <button 
              onClick={handleAdminClick}
              className="flex items-center space-x-2 text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
            >
              <div className="h-8 w-8 rounded-full bg-primary-600 flex items-center justify-center">
                <UserIcon className="h-5 w-5 text-white" />
              </div>
              <span className="hidden md:block text-gray-700 dark:text-gray-300">Admin</span>
            </button>

            {/* Admin dropdown menu */}
            {showAdminMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-md shadow-lg py-1 z-50 border border-gray-200 dark:border-gray-700">
                <button
                  onClick={() => {
                    alert('User Profile functionality coming soon!');
                    setShowAdminMenu(false);
                  }}
                  className="block w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                >
                  Profile
                </button>
                <button
                  onClick={() => {
                    alert('System Settings functionality coming soon!');
                    setShowAdminMenu(false);
                  }}
                  className="block w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                >
                  System Settings
                </button>
                <button
                  onClick={() => {
                    alert('Logout functionality coming soon!');
                    setShowAdminMenu(false);
                  }}
                  className="block w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
                >
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Click outside to close admin menu */}
      {showAdminMenu && (
        <div 
          className="fixed inset-0 z-40" 
          onClick={() => setShowAdminMenu(false)}
        />
      )}
    </header>
  );
}
